// import Logs from './components/Logs/Logs'

const App = () => {
  const clickHandler = () => {
    alert('我是App中的回调函数')
    /* 
      在React中，无法通过return fa'l'se
    */
    return false;
  }

  return (
    <div style={{ width: 200, height: 200, margin: '100px auto' }}>
      {/* 在React中时间需要通过元素属性来设置，
            和原生js不同，在React中事件的属性需要使用驼峰命名法：
              onclick -> onClick
              onchange -> onChange
            属性值不能直接执行代码，而是需要一个回调函数：
              onClick="alert(123)"
              onClick={()=>{alert(123)}}
      */}
      <button
        onClick={() => {
          alert(666)
        }}
      >
        我是陈冠希
      </button>
      <button onClick={clickHandler}>V我50</button>
      <br />
      <a href="https://www.baidu.com" onClick={clickHandler} >超链接</a>
    </div>
  )
}

/* 
  原生事件
  <button onclick="alert(123)">点我一下</button>

  <button id="btn">点我一下</button>
  document.getElementById('btn).onclick = fucntion(){}
  document.addEventListener('click',function(){},false)
*/

// 导出App
export default App
