import React from 'react'
import './MyDate.css'

export default function myDate(props) {
  return (
    <div>
      {/* 日期容器 */}
      <div className="date">
        <div className="month">{new Date(props.date).toLocalString('zh-CN')}月</div>
        <div className="day">{new Date(props.date).getDate()}</div>
      </div>
    </div>
  )
}
